import { Button } from 'react-bootstrap'
import './pagination.css'

function Pagination({ setCurrentPage, pages, currentPage }) {
    console.log(currentPage);

    return (
        <div className="pagination-group">


            <Button className='pagination-button' value={currentPage >= 1 ? currentPage - 1 : 0} onClick={(event) => setCurrentPage(Number(event.target.value))}> Previous </Button>

            {Array.from(Array(6), (item, index) => {
                return <Button key={index} className='pagination-button' value={index} onClick={(event) => setCurrentPage(Number(event.target.value))}>{index + 1}</Button>
            })}

            <Button>...</Button>
            <Button className='pagination-button'>{currentPage < 6 ? 7 : currentPage + 1}</Button>
            <Button className='pagination-button' value={currentPage <= 1 ? currentPage + 1 : 295} onClick={(event) => setCurrentPage(Number(event.target.value))}> Next </Button>
            <Button className='pagination-button' value={currentPage + 1} onClick={(event) => setCurrentPage(Number(event.target.value))}>+ {pages - currentPage}</Button>


        </div>
    );
}

export default Pagination;
