import { useState, useEffect } from 'react'
import CarsCard from './../CarsCard/CarsCard'
import SearchCar from './../Search/SearchCar'
import './CarsList.css'

const apiUrl = 'http://localhost:8000/api/cars'

function CardList() {
    const [isCarArrEmpty, setCarArrEmpty] = useState(false);
    const [allCars, setAllCars] = useState([])

    const [cars, setCars] = useState([])
    const [itensPage, setItensPage] = useState(40)
    const [currentPage, setCurrentPage] = useState(0)


    const getAllCars = async () => {
        try {
            const response = await fetch(apiUrl)
            const data = await response.json()
            setCars(data)
            setAllCars(data)

        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        getAllCars()
    }, [])

    const searchCarList = (query) => {
        let searchCars = allCars.filter((carsFilter) => {
            return carsFilter.BRAND.toLowerCase().includes(query.toLowerCase())
        })

        if (searchCars.length === 0) {
            setCarArrEmpty(true)
        }
        setCars(searchCars)

    }

    const sortByBrand = () => {
        const arrByBrand = [...cars].sort((a, b) => (a.BRAND) > b.BRAND ? 1 : -1)
        //console.log(arrByName)

        setCars(arrByBrand)
    }

    const sortByBrand2 = () => {
        const arrByBrand2 = [...cars].sort((a, b) => (a.BRAND) < b.BRAND ? 1 : -1)
        //console.log(arrByName)

        setCars(arrByBrand2)
    }

    const pages = Math.ceil(cars.length / itensPage)

    return (
        <div className='container-list'>
            <div>
                {Array.from(Array(pages), (item, index) => {
                    return <button value={index} onclick={(event) => setCurrentPage(Number(event.target.value))}>{index}</button>
                })}
            </div>

            <div className='search'>
                <SearchCar carList={searchCarList} />
            </div>

            <button className="btn" onClick={sortByBrand}>A-Z</button>
            <button className="btn" onClick={sortByBrand2}>Z-A</button>

            <div className="car-list">
                {isCarArrEmpty ?
                    <p> Não Encontrado</p>
                    : cars.map((oneCar, index) => {
                        return (
                            <div key={index}>
                                <CarsCard cars={oneCar} />
                            </div>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default CardList;
