import { useState, useEffect } from 'react'
import CarsCard from './../CarsCard/CarsCard'
import SearchCar from './../Search/SearchCar'
import './CarsList.css'

const apiUrl = 'http://localhost:8000/api/cars'

function CardList() {
    const [cars, setCars] = useState([])
    const [isCarArrEmpty, setCarArrEmpty] = useState(false);

    const updatedFoods = [foodObj, ...foods];

    const getAllCars = async () => {
        try {
            const response = await fetch(apiUrl)
            const data = await response.json()
            setCars(data)

        } catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        getAllCars()
    }, [])

    const searchCarList = (query) => {
        let searchCars = cars.filter((allCars) => {
            return allCars.BRAND.toLowerCase().includes(query.toLowerCase())
        })

        if (searchCars.length === 0) {
            setCarArrEmpty(true)
        }
        setCars(searchCars)

    }


    return (
        <div className='container-list'>
            <div className='search'>
                <SearchCar carList={searchCarList} />
            </div>
            <div className="car-list">

                {isCarArrEmpty ? 
                <p> error</p>
                    : cars.map((oneCar, index) => {
                        return (
                            <div key={index}>
                                <CarsCard cars={oneCar} />
                            </div>
                        )
                })
            }
            </div>
        </div>
    )
}

export default CardList;
