import { Button } from 'react-bootstrap'
import './pagination.css'
import { BsChevronDoubleLeft, BsChevronDoubleRight } from "react-icons/bs";

function Pagination({ setCurrentPage, pages, currentPage }) {
    console.log(currentPage);

    return (
        <div className="pagination-group">


            <Button className='pagination-button' value={ currentPage - 1 } onClick={(event) => setCurrentPage(Number(event.target.value))}> {}  </Button>

            {Array.from(Array(10), (item, index) => {
                return <Button className='pagination-button' value={index} onClick={(event) => setCurrentPage(Number(event.target.value))}>{index + 1}</Button>
            })}

            <Button className='pagination-button' value={ currentPage + 1 } onClick={(event) => setCurrentPage(Number(event.target.value))}>  </Button>

            <Button>{currentPage}</Button>

            <Button className='pagination-button' value={ currentPage + 1 } onClick={(event) => setCurrentPage(Number(event.target.value))}>...{pages - currentPage}</Button>


        </div>
    );
}

export default Pagination;
